package com.saiflimited.xpresenter.Adapters;

import android.annotation.TargetApi;
import android.app.Activity;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Build;
import android.util.Base64;
import android.view.View;
import android.view.ViewGroup;
import android.webkit.WebView;
import android.widget.ArrayAdapter;
import android.widget.ImageView;
import android.widget.TextView;

import com.saiflimited.xpresenter.Models.ContentData.ContentItemList;
import com.saiflimited.xpresenter.R;

import java.util.ArrayList;

public class ContentDetailDrillDownAdapter extends ArrayAdapter<ContentItemList> {
    private final Activity context;
    private final ArrayList<ContentItemList> contentItemLists;
    private final String format;

    public ContentDetailDrillDownAdapter(Activity activity, ArrayList<ContentItemList> contentItemLists, String format) {
        super(activity, R.layout.list_item_content_detail_drill_down_json, contentItemLists);
        this.context = activity;
        this.contentItemLists = contentItemLists;
        this.format = format;
    }

    @TargetApi(Build.VERSION_CODES.FROYO)
    public View getView(int position, View view, ViewGroup viewGroup) {
        View v = view;
        if (v == null) {
            ViewHolder viewHolder = new ViewHolder();
            if (format.toUpperCase().equals("JSON")) {
                v = this.context.getLayoutInflater().inflate(R.layout.list_item_content_detail_drill_down_json, null);
                viewHolder.lblContentDetailName = ((TextView) v.findViewById(R.id.lblContentDetailName));
                viewHolder.icon = (ImageView) v.findViewById(R.id.icon);
            } else {
                v = this.context.getLayoutInflater().inflate(R.layout.list_item_content_detail_drill_down_html, null);
                viewHolder.webView = (WebView) v.findViewById(R.id.webView);
            }
            v.setTag(viewHolder);
        }
        try {

            ViewHolder viewHolder = (ViewHolder) v.getTag();
            if (contentItemLists != null) {
                if (format.toUpperCase().equals("JSON")) {
                    viewHolder.lblContentDetailName.setText(contentItemLists.get(position).getContentItem().getName());
                    //TODO set icon
                    //Get Publisher background
//                String s = "";
//                byte[] bytes = Base64.decode(s, 0);
                    String encodedIcon = contentItemLists.get(position).getContentItem().getIcon();
                    String newEncodedIcon = encodedIcon.substring(encodedIcon.indexOf(",") + 1);
                    byte[] bytes = Base64.decode(newEncodedIcon, 0);
                    Bitmap icon = BitmapFactory.decodeByteArray(bytes, 0, bytes.length);
                    viewHolder.icon.setImageBitmap(icon);
                } else {
                    viewHolder.webView.loadData(contentItemLists.get(position).getContentItem().getHtmlBase64(), "text/html; charset=UTF-8", "base64");
                }
            }
            return v;
        } catch (Exception localJSONException) {
            localJSONException.printStackTrace();
        }
        return v;
    }

    @Override
    public long getItemId(int pos) {
        return pos;
    }

    static class ViewHolder {
        public TextView lblContentDetailName;
        public ImageView icon;
        public WebView webView;
    }
}